    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed
        String titulo, autor, ISBN, edicion; 
        int noPaginas;
        double precio;
        try{
           titulo = txtTitulo.getText();
           precio = Double.parseDouble(txtPrecio.getText());
           noPaginas = Integer.valueOf(txtNoPaginas.getText());
           ISBN = txtISBN.getText();
           autor = txtAutor.getText();
           edicion = txtEdicion.getText();

           mp = new ManPublica(titulo, precio, noPaginas);
           mp.alta(ISBN, autor, edicion);
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Revise los datos de entrada", "Error", JOptionPane.ERROR_MESSAGE);
        }
        JOptionPane.showMessageDialog(this, "Se Guaardo el libro", "Informaci√≥n", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_btnAceptarActionPerformed

